    /**
     * https://www.cnblogs.com/KevinFeng/p/15018798.html
     * 非标准json字符串转换成json格式字符串
     *
     * @param message
     * @return
     */
    //日志平台报文非标准json格式字符串转为json字符串
    public String stringToJson(String message) {
        String result = "";
        //替换=为":"
        if (message.contains("=")) {
            message = message.replace("=", "\":\"");
        }
        //替换{为{"
        if (message.contains("{")) {
            message = message.replace("{", "{\"");
        }
        //替换}为"}
        if (message.contains("}")) {
            message = message.replace("}", "\"}");
        }
        //替换[为["
        if (message.contains("[")) {
            message = message.replace("[", "[\"");
        }
        //替换]为"]
        if (message.contains("]")) {
            message = message.replace("]", "\"]");
        }
        //替换,为","
        if (message.contains(",")) {
            message = message.replace(",", "\",\"");
        }
        //替换"[为[
        if (message.contains("\"[")) {
            message = message.replace("\"[", "[");
        }
        //替换]"为]
        if (message.contains("]\"")) {
            message = message.replace("]\"", "]");
        }
        //替换"{为{
        if (message.contains("\"{")) {
            message = message.replace("\"{", "{");
        }
        //替换}"为}
        if (message.contains("}\"")) {
            message = message.replace("}\"", "}");
        }
        result = message;
        return result;
    }

    /**
     * java非json字符串转json字符串
     * https://blog.csdn.net/weixin_42802937/article/details/134551075
     */
    public String toJson(String s) {
        s = s.substring(0, s.length()).replace("{", "{\"");
        s = s.substring(0, s.length()).replace("}", "\"}");
        s = s.substring(0, s.length()).replace(", ", "\", \"");
        s = s.substring(0, s.length()).replace("=", "\":\"");
        s = s.substring(0, s.length()).replace("\"[", "[");
        s = s.substring(0, s.length()).replace("]\"", "]");
        s = s.substring(0, s.length()).replace("}\", \"{", "}, {");
        return s;
    }


    @Test
    public void fun1() {
        String a = "{categoryCode=R90046591,pageSize=50,pageNo=1,vendorCode=709028701}";
        String res = stringToJson(a);
        System.out.println(res);
    }

